INSERT INTO `age_level` VALUES (1, 18, '未成年');
INSERT INTO `age_level` VALUES (19, 29, '青年');
INSERT INTO `age_level` VALUES (30, 49, '中年');
INSERT INTO `age_level` VALUES (50, 100, '老年');
INSERT INTO `department` VALUES (1, '呼吸内科', '内科系统', '人民医院呼吸与危重症医学科始建于1962年。科室集医疗、教学和科研一体，全科同仁长期以来致力于对各种呼吸系统疾病及危重症精准诊断和规范治疗，“以疑难重症为重心，和谐共进讲奉献；以全面高效为目标，打拼一流树品牌”。2016年第九届中国健康总评榜我科被选为“年度最受欢迎特色专科”。', '门诊楼南侧七号楼二楼', 0);
INSERT INTO `department` VALUES (2, '消化内科', '内科系统', '消化内科做为独立的学科始建于1990年。90年代历任主任及副主任为吴晶新教授，赵景涛教授及屈汉廷教授。 九十年代后期，学科引进了年轻的学术精英刘玉兰博士。刘教授1996年至1998年任消化科副主任，1998年至2001年任消化科常务副主任、肝病研究所副所长，2001年后担任消化科主任，成为学科发展史上的第三任学术带头人，2006年起担任人民医院科研副院长。21世纪开始在刘玉兰主任的带领下，学科进入了快速发展时期。\r\n\r\n  经过三代人的努力，目前消化科已经拥有病房、门诊（并设有非酒精性脂肪性肝病，炎症性肠病及胃食管反流专业门诊）、内镜中心、胃肠动力室、超声检查室和实验室，成为集临床、科研、教学为一体的综合能力较强的科室。目前消化科是卫计委专科医师培训基地；国家药品监督管理局临床药物试验机构；内科博士点。\r\n\r\n  多年来消化学科在医、教、研三方面均衡发展，确定了学科发展方向；同时加强了青年医护人员的培养，建立了完善的人才梯队。现有医生27名，正高4名，副高8名，主治医师5名，住院医师10名，绝大多数具备博士学位；25名护士；技师2人。', '门诊楼1层中厅', 0);
INSERT INTO `department` VALUES (3, '心血管内科', '内科系统', '人民医院心血管内科具有悠久的历史，我国著名的心血管病专家马万森教授于上世纪50年代奠基并创建了北京大学人民医院心血管病专业，对心血管疾病诊治及相关技术进行潜心研究。经过共同努力，于80年代正式成立了心血管内科。经过几代人的奋斗，北京大学人民医院心血管内科目前是“教育部国家级重点学科”，“卫计委国家临床重点专科”，“急性心肌梗死早期预警和干预北京市重点实验室”，“美国心脏学会专业示范中心”，国内综合医院中首家通过美国SCPC认证的胸痛中心，卫计委心血管冠脉介入治疗培训基地，卫计委心律失常介入治疗的培训基地，北京市住院医师规范化培训亚专科基地。并获得了“全国巾帼文明岗”和“北京市模范职工之家”称号。', '门诊楼5层510', 0);
INSERT INTO `department` VALUES (4, '肾内科', '内科系统', '人民医院肾内科创建于1988年，拥有一支不断进取、以中青年医疗骨干为核心的学科队伍，经过数十年的不懈努力，现已发展成为集医疗、教学和科研于一体的技术全面、临床扎实的国内一流肾病科室。我科现为教育部985国家重点学科、肾脏内科专科医师培训基地、国家药物临床药理基地，北京大学人民医院重点学科，并且是《中国血液净化》杂志的所在地、中华护理学会血液净化培训基地。肾脏内科由病房、门诊、血液净化中心（含血液透析（HD）中心和腹膜透析(PD)中心）、慢性肾脏病（CKD）中心、连续性肾脏替代治疗(CRRT)中心、肾脏病理室、临床检验室及《中国血液净化》杂志编辑部组成。科室拥有健全的规章制度、岗位职责和诊疗常规，并坚持每月各部门的医疗质量考核、汇报制度，促进了持续性质量改进。血液透析中心和腹膜透析中心的管理水平国内领先。积极开展肾活检病理诊断技术，肾活检完成量及肾脏病理诊断数量均居北京市前列', '门诊楼3层北侧', 0);
INSERT INTO `department` VALUES (5, '内分泌科', '内科系统', '人民医院内分泌代谢科创建于1986年，多年来承担了大量的医教研工作，在内分泌代谢疾病的临床及研究中均处于国内领先地位。北京大学人民医院于2011年获得国家卫计委重点专科（内分泌代谢病专业）建设项目，目前是北京市唯一一家由北京市科学技术委员会授予的糖尿病领域临床研究中心。人民医院内分泌代谢科具有内分泌门诊、内分泌病房、内分泌临床实验室、糖尿病科研实验室。学科设置了1型糖尿病、2型糖尿病、营养和肥胖症、垂体疾病、肾上腺疾病、甲状腺疾病、骨盐代谢和骨质疏松学组。内分泌门诊年接诊患者近15万人，内分泌病房年收治病患千余人，诊治疾病涵盖了下丘脑垂体、甲状腺、肾上腺、性腺、糖脂代谢、骨盐代谢、钾钠代谢、水电解质酸碱平衡相关的常见病和罕见疑难病。', '门诊楼5层南侧', 0);
INSERT INTO `department` VALUES (6, '风湿免疫科', '内科系统', '人民医院风湿免疫学科/风湿免疫研究所为国家临床重点学科，风湿病教育部创新团队，北京市风湿病重点实验室和北京市国际科技合作基地，国内唯一的亚太（APLAR）风湿病中心。目前，全科有100余人的临床及科研队伍，包括教授和副教授25人，博士生导师7人，硕士生导师及副博导16人，主治医师及住院医师10人，助研和实验员/技师10余人，在读博士后、博士、硕士研究生和研究人员30余人。护理团队25人，副主任护师1人，主管护师3人。该中心成员包括前国际风湿病联盟（ILAR）主席和前亚太风湿病联盟（APLAR）主席、中华医学会风湿病学分会名誉主委、中国免疫学会临床免疫分会主任委员、WHO肌肉骨骼疾病委员会委员、中华医学会风湿病学分会常委及委员等。其他兼职包括《Clin Rheum》和《IJRD》副主编，《北京大学学报·医学版》副主编，《中华风湿病学杂志》总编，《医学参考报（风湿免疫专刊）》主编，《Nat Rev Rheum》、《Ann Rheum Dis》及《中华医学杂志》编委。', '门诊楼5层北侧', 0);
INSERT INTO `department` VALUES (7, '感染科', '内科系统', '人民医院感染病科秉承了先辈对于传染病严谨治学、勇于奉献的精神，伴随着我国传染病历史的发展，几代人始终兢兢业业地致力于传染病的诊治与研究事业。魏来教授任人民医院感染病科首任主任，现由高燕教授担任科室主任，目前已拥有医生12人，护士9人，其中博士研究生导师1人，主任医师/教授1人，副主任医师1人。', '门诊楼5层北侧', 0);
INSERT INTO `department` VALUES (8, '老年科', '内科系统', '30余年来老年医学科始终秉承医疗以“共病管理”为主导，护理以“细致精准”为宗旨，全体工作人员通力合作，老年医学科在医疗、教学、科研及辐射能力等多方面齐头并进。老年医学科致力于为老年患者提供全人照顾的个体化医疗服务。随着人口老龄化的进展，老年医学科关注：骨质疏松、老年糖尿病、老年便秘、老年综合评估、老年高血压、老年动脉粥样硬化、认知障碍等多种困扰老年患者的疾病。', '门诊楼5层北侧', 0);
INSERT INTO `department` VALUES (9, '神经内科', '内科系统', '人民医院神经内科成立于1983年，目前共有床位56张（包括癫痫中心6张床位）。拥有一批素质较高的专业化人才队伍，现有正高4人（教授2名，主任医师2名），副教授、副主任医师9名，主治医师8名，住院医师3名，护士20名，技师7名。有国外工作、学习经历者多名。', '门诊：门诊楼3层北侧，病房：8层A区', 0);
INSERT INTO `department` VALUES (10, '肝胆外科', '外科系统', '人民医院肝胆外科多年来一直在肝胆外科领域享有盛誉，现为国家211工程、卫生部重点专科、北京市重点学科、国家教委博士点学科、北京市重点实验室、器官移植研究所、北京大学肝癌研究中心，现有教授8人、副教授6人，主治医师4人，住院医师1人，其中博士生导师2名、硕士生导师5名，承担着多项卫生部行业专项、863、973、985计划、九五至十二五攻关、国家科技支撑计划、国家自然科学基金等国家大型科研项目。', '门诊：门诊楼2层北侧，病房：病房楼7层A区', 0);
INSERT INTO `department` VALUES (11, '胃肠外科', '外科系统', '人民医院胃肠外科前身为1942年的中央医院普外科胃肠专业组，具有七十余年的历史。著名教授黄萃莛、杜如昱、张嘉庆、祝学光曾先后担任科主任。1997年以后普通外科根据学科发展的需要，按照专业特点划分为肝胆外科、胃肠外科、乳腺外科中心、外科监护、微创外科等科室。于永祥教授担任胃肠外科主任，王杉教授为副主任。2003年王杉教授担任胃肠外科主任，叶颖江教授担任副主任。2006年王杉教授任人民医院院长，叶颖江教授担任副主任。2013年叶颖江教授担任胃肠外科主任。', '特需门诊：西直门院区门诊楼5层，专家及普通门诊：西直门院区门诊楼2层北侧，病房：西直门院区6层A区；白塔寺院区42病房', 0);
INSERT INTO `department` VALUES (13, '乳腺外科', '外科系统', '人民医院乳腺中心于1992年8月成立，是全国第一家以医疗、科研、教学、普查于一体的综合诊疗模式为特征并命名的乳腺中心。经过25年的不懈努力，北京大学人民医院乳腺中心在乳腺癌早诊早治和综合治疗领域取得了令人瞩目的成就。现中心开放床位50张，医生16人，护士17人。包括主任医师6人，副主任医师4人，主治医师2人，主管护士3人。每年完成超过4万人次的乳腺癌普查，收治原发性乳腺癌病例近800例，位居北京市各医院前四名。\r\n\r\n  ', '门诊楼2层北侧', 0);
INSERT INTO `department` VALUES (15, '泌尿与碎石中心', '外科系统', '人民医院泌尿外科由病房、门诊及实验室构成。目前在新院和老院各有一个病房，老院31病房开放床位32张，新院13A病房开放床位45张，共计床位数为77张。在编职工64人（医生23名，护士40名,技术员1名）；医生中正高级职称9名，副高职称10人.全科医生23人中有17人具有博士学位；3人具有硕士学位。护士40人，其中主管护师8名，护师11名。泌尿外科门诊拥有5个诊室，1个男科检查室，1个尿动力学检查室,1个泌尿外科治疗室，1个临床药理基地办公室。', '门诊楼2楼北侧', 0);
INSERT INTO `department` VALUES (16, '心外科', '外科系统', '人民医院心脏外科成立于1987年，经过几代人的努力，已经成为国内一流的心脏外科中心，开展了包括冠状动脉硬化性心脏病，先天性心脏病，心脏瓣膜疾病，主动脉疾病，心脏肿瘤，心律失常以及心脏移植治疗心力衰竭治疗在内的各种心脏大血管手术。其中微创冠状动脉旁路移植术处于全国的领先水平，开展了非体外循环不停跳冠状动脉搭桥术、各种微创小切口冠状动脉搭桥手术、局麻清醒状态下的冠状动脉搭桥术、微创获取搭桥移植血管等多种术式，获得教育部科技进步一等奖。', '门诊楼3层南侧', 0);
INSERT INTO `department` VALUES (17, '血管外科', '外科系统', '人民医院血管外科自2000年开始起步，是国内较早的独立血管外科之一。目前已经发展成为国内外享有较高知名度的血管疾病综合诊疗中心，也是全国普通外科重点学科和全国外周血管疾病介入培训中心。拥有开放床位30张的独立病区，专家团队10人，年手术量1,200余例，年门急诊量约15,000人次，在医院中心手术室拥有独立的杂交手术间，配备先进的DSA造影系统，可以开展各类血管疾病的诊断和治疗。', '门诊楼3层南侧', 0);
INSERT INTO `department` VALUES (18, '胸外科', '外科系统', '人民医院胸外科创建于1952年。经过65年艰苦奋斗，尤其是进入本世纪以来的跨越式发展，已经成为以胸部微创技术为鲜明特色、以肺癌的早诊早治为突出优势的全国知名学科。学科带头人王俊教授是中国胸部微创手术的开拓者和奠基人、中国肺癌微创综合诊疗体系及”王氏技术”的创立者、中央保健会诊专家及中央保健先进工作者。科室近年先后被评为卫生部国家临床重点建设专科、北京市肺癌规范化诊治示范中心、中国医师协会胸腔镜医师培训学院、北京大学模范职工小家等，是国内外公认的我国胸腔镜胸部微创手术技术力量最为雄厚的“排头兵”单位。', '门诊楼3层南侧', 0);
INSERT INTO `department` VALUES (19, '神经外科', '外科系统', '人民医院神经外科是以“精准、微创”为诊疗特色，秉承“患者为中心”的诊疗理念，门急诊救治兼备，涵盖中枢神经系统疾病显微神经外科、功能神经外科、内镜神经外科治疗的专业科室。科室由栾文忠教授于1984年创建，经过30余年的传承积淀和不懈努力，现已成为北京大学医学部医疗、教学、科研和优质护理为一体、专业特色突出的学科。', '门诊楼3层北侧', 0);
INSERT INTO `department` VALUES (20, '骨肿瘤科', '外科系统', '人民医院骨肿瘤科是从事骨与软组织肿瘤诊疗的专业机构之一， 自1997年建科以来，历经多年发展，已成为集临床、科研、教学为一体，在国内外享有很高知名度的骨与软组织诊断治疗中心，现为国家教委重点学科，北京大学人民医院重点科室，全国骨肿瘤诊疗研究中心。科室于2003年改名为骨与软组织肿瘤治疗中心。中心拥有国内最著名的骨与软组织肿瘤治疗专家和高级专业技术队伍，每年诊治大量来自全国各地的骨与软组织肿瘤病人', '门诊楼2层南侧', 0);
INSERT INTO `department` VALUES (21, '骨关节科', '外科系统', '人民医院骨关节科创建于1987年，是以治疗各种骨关节病为主，以膝、髋关节置换及关节镜为特色的科室。科室现有医生、技术员和护士50余名，其中主任医师9名、副主任医师3名、主管技师2名。人民医院骨关节科事业迅猛发展，医、教、研水平全面提高。现已发展成为以膝、髋关节置换及关节镜手术为科室特色，各项诊疗设备完备，专业组健全，新技术、新疗法日益增加，科研课题不断增多的综合性科室。', '门诊楼2层南侧', 0);
INSERT INTO `department` VALUES (22, '创伤骨科', '外科系统', '人民医院创伤骨科是集病房、急诊、门诊及研究室为一体的完整学科，现为北京大学博士点学科，教育部211工程国家重点学科、卫生部国家点专科（骨科）、全国严重创伤规范化救治培训基地、教育部创新团队，中华医学会创伤学分会前任主任委员单位、中华医学会骨科分会副主任委员单位、北京医学会骨科学分会主任委员单位；学科人员梯队合理，包括正高级职称8人、副高级职称5人、中级职称7人、初级职称（医师、护士）及在读研究生42人。', '门诊楼2层南侧', 0);
INSERT INTO `department` VALUES (23, '脊柱外科', '外科系统', '人民医院脊柱外科成立于 2001年，系中国较早成立的几家专门从事脊柱疾病治疗的专业科室之一。科室自建立之初在刘海鹰主任的带领之下一直良性健康发展，目前具有脊柱外科床位42张，年脊柱手术逾800例，其中颈椎手术300例，腰椎400例，脊柱侧弯及椎间盘镜手术约100例。秉承着严谨奉献、奋斗不息的精神，我院脊柱外科在刘海鹰主任的带领下，在国内首先开展了各类脊柱手术技术，在随后的近15年时间里，更是勇于开拓创新、积极反思', '门诊楼2层南侧', 0);
INSERT INTO `department` VALUES (24, '整形美容科', '外科系统', '人民医院整形美容科成立于2008年，建科成员均来自于中国医学科学院整形外科医院。科室拥有独立的门诊和病房，是北京市美容整形协会主诊医师培训指定机构。现学科带头人穆籣（穆兰花）教授是国、内外著名乳房整形美容专家，现任中国女医师协会整形美容专家委员会主任委员,中国医师协会美容与整形医师分会乳房亚专业委员会 副主任委员,中华医学会整形外科分会委员，中国科协首批全国整形美容科学传播专家', '门诊楼2层北侧', 0);
INSERT INTO `department` VALUES (27, '儿科', '妇产科儿科', '科成立于1953年，有着辉煌而悠久的历史。几任儿科主任都是在儿科学界享有盛誉和广泛影响力的专家，其中周华康教授是首都儿科研究所的创始人之一，袁承文教授曾担任《中华儿科杂志》的副主编，儿科也曾获得北京市级的先进集体奖励。经过几代医学专家及全体医务人员的共同努力，儿科得到全面发展，2001年成为国家重点学科，尤其以血液、神经及新生儿专业为特色专业。儿科门急诊24小时开放，诊治16岁以下儿童及青少年。各专业均设有专家门诊', '门诊楼外南侧', 0);
INSERT INTO `department` VALUES (28, '眼科门诊', '五官科', '人民医院眼科创建于1942年。眼科专业在原中央医院(人民医院前身)改组时设立，已故原协和医院著名眼科专家罗宗贤教授为首任特约医师，指导孙信孚医生工作。1946年孙信孚医生离院，由付守静及索曼霞医生负责。建科初期的主要临床工作仅为院内会诊及一般的眼科门诊。至60年代，杨均主任首先开设眼科病房，并开展了青光眼、白内障手术。进入70年代，在费佩芬、吕永顺、冯耀庭几位主任的领导下，眼科床位数增加至8张。', '门诊楼外南侧', 0);
INSERT INTO `department` VALUES (29, '耳鼻喉科', '五官科', '人民医院耳鼻喉科成立于解放前，1942年，由林必锦教授主持科内工作，并聘请我国耳鼻喉科鼻祖刘瑞华教授为顾问。1943年，刘瑞华教授全面主持科内工作。1953年，时任主治医师的刘芳柽负责我科的医教研全面工作。1960年耳鼻喉科从外科中分离，成为独立的耳鼻喉咽喉科和耳鼻咽喉教研室。1995年，划分为三个专业组：耳科组，鼻科组，以及咽喉头颈组并延续至今。1999年，耳鼻喉科博士研究生的申报顺利通过，时任科主任的于德林教授也成为了北京大学医学部的首位耳鼻喉科博士生导师。2001年，余力生教授接任耳鼻喉科主任至今。', '人民医院门诊4层', 0);
INSERT INTO `department` VALUES (30, '口腔科', '五官科', '人民医院口腔科创建于1917年，历任主任分别为俞家振、张仁德、夏善福、高承志，经过数代人近百年的努力，人民医院口腔科已发展成为集医疗、教学和科研为一体，亚专业齐备，人才梯队完整的现代化科室，在医教研等方面都居于先进行列。', '门诊楼4层南侧', 0);
INSERT INTO `department` VALUES (31, '皮科', '其他科室', '人民医院皮肤科为国家重点学科、卫生部临床重点专科，国家“211”和“985”重点学科、教育部博士点授予单位、卫生部住院医师规范化培训基地、国家药物机构皮肤性病专业基地、中华医学会皮肤美容示范基地。', '门诊楼4层南侧', 0);
INSERT INTO `department` VALUES (32, '急诊科', '其他科室', '急诊科成立于1983年。是我国最早建立急诊医学科的医院之一。30余年的不懈努力，使之成为北京地区业界最繁忙、口碑最佳的学科之一。并于2012年成为全国重点专科。对心脑血管急症、血液相关急症有较深入的研究及丰富的临床经验，尤其是2016年创伤中心的成立，使北京大学人民医院的急危重症病患的救治能力有了很大的提升。', '门诊楼4层南侧', 0);
INSERT INTO `department` VALUES (33, '中医科', '其他科室', '人民医院中医科成立于1956年。医院聘请全国著名中医徐衡之、孙仲寰建立中医科，并出任内科血液病专业组中医顾问，开创血液病研究治疗的先河，成为中西医结合治疗疾病的典范。 经过六十余年的发展，中医科在祖国传统医学与西方现代医学在临床诊疗结合道路上不断探索，现已发展成为集中医特色医疗教学科研为一体的综合性科室。', '门诊楼一层南侧', 0);
INSERT INTO `department` VALUES (34, '精神科', '其他科室', '推进健康中国建设，倡导健康生活方式，加强心理健康服务的背景下，北京大学人民医院精神科成立。我科采取现场挂号和预约挂号相结合的方式，药物治疗与非药物治疗（心理治疗）相结合的治疗原则，为广大受情绪、心理、睡眠、记忆等问题困扰的人们提供专业、系统、连续性的医疗服务。主要诊疗范围包括：失眠等睡眠问题，抑郁等情绪问题，焦虑、神经症、认知障碍等。', '门诊楼一层南侧', 0);
INSERT INTO `department` VALUES (35, '麻醉科', '其他科室', '人民医院麻醉科成立于1954年，是我国最早一批成立麻醉科的医院。历任主任谢柏樟教授、于德水教授、杨拔贤教授。现任主任冯艺教授，副主任乔青副主任医师和安海燕主任医师。经过几代人民麻醉人的不懈努力，现已经发展成集临床、教学、科研为一体的综合性科室。现有在职医师64名，技护19名，95%以上医生具有硕士、博士学位，其中5位主任医师，12位副主任医师以及多位校级、院级优秀教师，形成了一个人员众多、人才济济、多元化、多层级而又充满活力的团体。', '西直门南大街11号', 0);
INSERT INTO `department` VALUES (36, '疼痛医学科', '其他科室', '人民医院疼痛医学科前期为麻醉科疼痛治疗专业组，成立于2007年6月，2013年成为独立科室。是一个集临床、科研和教学为一体的综合科室。拥有先进的临床和科研团队及国际一流的疼痛诊疗设备，在慢性疼痛的诊断和微创介入治疗方面处于国内领先地位。', '门诊楼4层北侧', 0);
INSERT INTO `department` VALUES (37, '重症医学科', '其他科室', '人民医院重症医学科是我国第一家整合型的成人重症医学科，包含着内科、外科、心血管及北京大学人民医院北院区四个病区，共49张床位，是国家卫生计生委首批临床重点专科。随着科技的进步和发展，北京大学人民医院重症医学科拥有几乎全部重症医学诊疗设备。', '门诊楼4层北侧', 0);
INSERT INTO `department` VALUES (38, '放射科', '诊断相关科室', '人民医院放射科经历了近一个世纪的发展，成为全数字化、种类齐全的现代化影像检查、诊断、治疗科室，提供数字X线摄影、造影检查、CT、MRI、以及各类介入检查治疗的服务。现有大型仪器设备30台，包括直接数字化X线成像DR设备8台、计算机X线成像CR设备2台、数字化乳腺钼靶成像设备1台、数字化胃肠机2台、CT机5台、磁共振成像扫描仪4台、直接数字化移动床旁机5台、普通床旁机3台。', '门诊楼1层中厅', 0);
INSERT INTO `department` VALUES (39, '超声诊断科', '诊断相关科室', '人民医院超声科是国内综合性的超声医学科，集超声诊断与超声引导介入治疗于一体的超声科室，超声医学科拥有专业的人才梯队，世界一流的超声设备，承担北京大学医学部医教研任务，是北京大学博士、硕士培养点，北京市住院医师规范化培养基地，是中国医师协会超声分会肌骨超声首批培训基地。', '西直门院区门诊楼三层中厅超声科', 0);
INSERT INTO `department` VALUES (40, '核医学科', '诊断相关科室', '人民医院核医学科前身为1958年创立的同位素室，现已发展成为独立的专业科室，工作内容包括放射性核素显像、放射性核素治疗及体外放射分析。现有医生、技术员和护士19名，其中高级职称3人，中级职称9人，初级职称7人。目前科室具有PET/CT 1台、SPECT/CT 2台、双光能X线骨密度仪1台。开展核素显像30余种，年检查例数上万人次。目前核医学科影像诊断水平位于全国前列', '门诊楼1层南侧', 0);
INSERT INTO `department` VALUES (41, '检验科', '诊断相关科室', '人民医院检验科是集临床检验、教学、科研于一体的综合性医学实验室。承担着全院日常的检验服务，为临床疾病的诊断、治疗、预防和预测提供准确科学的实验室依据。科室的宗旨是：以病人为中心、质量为基准、服务为根本、科学为原则。检验科现开展检验项目300余项，每年承担标本量达200余万；2008年，成为国内较早通过ISO15189医学实验室认可的实验室之一；2010年获批国家临床重点专科建设项目；并于2015年通过美国病理学家协会（CAP）的评审，获得CAP实验室认可。', '门急诊化验室（门诊楼2层中厅）', 0);
INSERT INTO `department` VALUES (42, '病理科', '诊断相关科室', '民医院病理科成立于1955年，是国内在综合医院内较早设置的病理科。历任病理科主任在国内病理界享有盛名。人民医院病理科在乳腺肿瘤、妇产科肿瘤、骨及软组织病变的病理诊断水平位于国内领先地位，在国内病理学界具有较高的影响力', '门诊楼4层北侧', 0);
