INSERT INTO `qa` VALUES (1, '就诊卡遗失，重新办理新卡后是否可以绑定？', '可以，先绑定新卡，再将原卡解除绑定。但同一张就诊卡在解绑30天以后才可以重新再次绑定');
INSERT INTO `qa` VALUES (2, '如果有患者带上相关文件要求盖章怎么引导？', '相关文件不同，盖章地点不同。');
INSERT INTO `qa` VALUES (3, '开药能不能家属代为办理？', '不能。');
INSERT INTO `qa` VALUES (4, '检查科室的上下班时间，可以预约检查的时间段？', '不同检查科室工作时间及预约时间不同，详情请咨询相应检查科室预约处。');
INSERT INTO `qa` VALUES (5, '医院三甲证明怎么办理？', '请直接电话咨询医保办028-88886666。');
INSERT INTO `qa` VALUES (6, '就诊卡信息错了可以先挂号然后再去医院改吗？', '可持就诊卡及本人身份证原件到门诊办公室信息窗口更正。');
INSERT INTO `qa` VALUES (7, '复查应该挂医生的号还是随访门诊？', '原则上挂医生的号（随访门诊有一定的适应对象）');
INSERT INTO `qa` VALUES (8, '如果病人在出院时未提取病历已办理出院手续，病人在外地想要提取病历怎么办？出院证明遗失是否能提取病历？', '请直接电话咨询病案科028-85422269。');
INSERT INTO `qa` VALUES (9, '如何办理异地就医？外地医保能不能用？', '请直接电话咨询医保办028-85423429。');
INSERT INTO `qa` VALUES (10, '鉴定中心的上下班时间？做鉴定的时候要带哪些东西？', '请直接电话咨询028-85423407/85422028。');
INSERT INTO `qa` VALUES (11, '特殊门诊周末能不能结算？', '不能结算，可以记账。');
INSERT INTO `qa` VALUES (12, '就诊卡违约了能不能在医院现场直接进行解锁？', '不能，违约当天开始半年后自动解锁。');
INSERT INTO `qa` VALUES (13, '检查单（检查结果）丢失能不能补打？', '不同的检查要求不一样，详情请咨询相应的检查科室。');
INSERT INTO `qa` VALUES (14, '病人遗失东西应该联系哪里？', '可到安全保卫部或门诊办公室询问，是否有人捡到遗失物品交到这里。');
INSERT INTO `qa` VALUES (15, '补办了就诊卡，之前的就诊卡又找到了，那两张卡可以同时使用吗？', '不能。');
INSERT INTO `qa` VALUES (16, '临时卡能不能电话预约挂号，能否长期使用？', '临时卡只限急诊当日挂号就诊。');
INSERT INTO `qa` VALUES (17, '门诊、急诊能做哪些检查?有哪些仪器？', '此问题涉及面较广，总的来说，医生根据病人病情开具相应的检查。');
INSERT INTO `qa` VALUES (18, '付款能不能刷微信、支付宝、信用卡？', '可以。');
INSERT INTO `qa` VALUES (19, '能不能做驾照体检？', '不能。');
INSERT INTO `qa` VALUES (20, '活检的流程，挂什么号？什么时候拿结果？', '带上标本、病人身份证原件和复印件及病人本人的就诊卡到门诊4楼病理科办理。');
INSERT INTO `qa` VALUES (21, '检查结果能在医院保存多久时间？', '门诊检查结果原则上不少于15年，实际中视情况而定。');
INSERT INTO `qa` VALUES (22, '急诊分不分科室？', '分，急诊有分诊台。');
INSERT INTO `qa` VALUES (23, '医院现场挂号每天的号源有多少？', '我院实行预约挂号制，所有专科号源均提前放出。当天现场挂号只有便民门诊、专科普通门诊和很少的未预约完的专科门诊号源。');
INSERT INTO `qa` VALUES (24, '\r\n新办的带芯片的社保卡能不能挂号？', '目前还不能。');
INSERT INTO `qa` VALUES (25, '咨询与院内合作应该引导到哪个部门？', '医院办公室。');
INSERT INTO `qa` VALUES (26, '非人工窗口挂的号如何换取挂号发票？', '请您携带患者本人的身份证原件和复印件及就诊卡，并提供具体的挂号日期到门诊一楼A1区门诊办公室信息窗口处理。');
INSERT INTO `qa` VALUES (27, '挂号单遗失了，卡还在怎么办？', '如果您的挂号单不小心遗失了，可在就诊当日持卡到相应就诊区域的护士咨询台，请护士老师帮助查询挂号信息，如果系统里有您的挂号信息，可直接看诊。');
INSERT INTO `qa` VALUES (28, '挂号单上注明的时间为上午9点就诊，我能晚点来吗？', '原则上我们将按号签顺序、结合患者到达医院的报到时间安排患者有序就诊。如果晚来，报到时间：上午应不超过11:30，下午不超过15:30。');
INSERT INTO `qa` VALUES (29, '临时卡能不能电话预约挂号，能否长期使用？', '临时卡只限急诊当日挂号就诊。');
INSERT INTO `qa` VALUES (30, '新办的带芯片的社保卡能不能挂号？', '目前还不能。');
INSERT INTO `qa` VALUES (31, '看病是不是一定要有就诊卡？', '医院看病必须首先办理就诊卡，并且得提供病人本人的身份证或户口簿等证件原件办理实名制就诊卡。');
INSERT INTO `qa` VALUES (32, '\r\n就诊卡遗失了怎么办？', '凭病人有效身份证件原件到门诊一、二、三楼圆弧大厅或第二门诊一楼挂失补办');
